	constructor() {
		rewardsToken = new AALToken();
		priceConsumer = new PriceConsumer(0xAa7F6f7f507457a1EE157fE97F6c7DB2BEec5cD0);
		stackingToken = new TokenBidon(100000);
		listOfToken.push();
